  rp_worker_id, rp_creation, rp_content
  )
values (#{rp_id,jdbcType=INTEGER}, #{rp_vedio,jdbcType=VARCHAR}, #{rp_audio,jdbcType=VARCHAR}, 
  #{rp_worker_id,jdbcType=INTEGER}, #{rp_creation,jdbcType=TIMESTAMP}, #{rp_content,jdbcType=VARCHAR}
  )

mapper.select:
