mapper.select:
 select 
  
 from worker  where 1
 
		and wk_id = #{wk_id}
 
 
		and wk_num = #{wk_num}
 
  
		and wk_type = #{wk_type}
  
  
		and wk_openid = #{wk_openid}
 
  
		and wk_phone = #{wk_phone}
  
   
		and wk_isdel = #{wk_isdel}
   
    
 	 and wk_password = md5( #{wk_password,jdbcType=VARCHAR}),
